/**
 * N8N Code Box: Subtitle Array Batch Splitter
 * Purpose: Splits subtitle arrays into smaller batches of 50 items for processing
 * Input: Items with 'subtitles' array property
 * Output: Multiple items, each containing a batch of subtitles with metadata
 */


const BATCH_SIZE = 50; // Split subtitle array into batches of 50
const outputItems = [];

for (const item of $input.all()) {
  const subtitles = item.json.subtitles || [];

  // If no subtitles, create an empty item
  if (subtitles.length === 0) {
    outputItems.push({
      json: {
        ...item.json,
        batch_subtitles: [],
        batch_number: 0,
        batch_size: 0,
        total_subtitles: 0,
        total_batches: 0
      },
      binary: item.binary
    });
    continue;
  }

  // Calculate total number of batches
  const totalBatches = Math.ceil(subtitles.length / BATCH_SIZE);

  // Create batches
  for (let i = 0; i < subtitles.length; i += BATCH_SIZE) {
    const batchNumber = Math.floor(i / BATCH_SIZE) + 1;
    const batch = subtitles.slice(i, i + BATCH_SIZE);

    // Create new item for each batch
    const batchItem = {
      json: {
        ...item.json, // Copy all original fields
        batch_subtitles: batch,
        batch_number: batchNumber,
        batch_size: batch.length,
        total_subtitles: subtitles.length,
        total_batches: totalBatches,
        batch_start_index: i + 1,
        batch_end_index: Math.min(i + BATCH_SIZE, subtitles.length)
      },
      binary: item.binary
    };

    outputItems.push(batchItem);
  }
}
return outputItems;

/**
 * N8N Code Box: Translation Batch Merger and SRT Data Combiner
 * Purpose: Merges all translation batches back together and combines with original SRT parse data
 * Input: Items with translation 'output' arrays and access to 'Parse SRT' node data
 * Output: Single item with all translations sorted by index and combined with SRT metadata
 */

const allTranslations = [];
// Collect all translations from all items
for (const item of $input.all()) {
  const output = item.json.output || [];

  for (const translation of output) {
    allTranslations.push(translation);
  }
}
// Sort by index
allTranslations.sort((a, b) => a.index - b.index);
// Get data from Parse SRT node
const parseSRTData = $('Parse SRT').all();
// Create output item combining both data sets
const outputItem = {
  json: {
    // Translation data
    subtitles_translated: allTranslations,
    total_translations: allTranslations.length,

    // Specific Parse SRT data
    parse_srt_data: parseSRTData.map(item => ({
      srt_file: item.json.srt_file,
      filename: item.json.filename,
      submittedAt: item.json.submittedAt
    })),

    // If there's only one item in Parse SRT, also include its fields directly
    ...(parseSRTData.length === 1 ? parseSRTData[0].json : {})
  }
};
return [outputItem];

/**
 * N8N Code Box: Binary File Content Processor
 * Purpose: Reads Base64 binary file data, converts to UTF-8 string, and cleans up carriage returns
 * Input: Items with binary data in 'srt_file' property
 * Output: Items with file content added to JSON and binary data removed
 */

// Read Base64 binary file and remove file after processing
for (const item of $input.all()) {
  // Get the binary file
  const binaryData = item.binary['srt_file'];

  if (binaryData) {
    // Content comes in Base64, decode it
    const fileContentBase64 = binaryData.data;

    // Decode from Base64 using Buffer (available in n8n)
    const buffer = Buffer.from(fileContentBase64, 'base64');
    let contentAsString = buffer.toString('utf-8');

    // Remove \r (carriage returns)
    contentAsString = contentAsString.replace(/\r/g, '');

    // Add content to the item's JSON
    item.json.file_content = contentAsString;
    item.json.file_name = binaryData.fileName;
    item.json.file_mime_type = binaryData.mimeType;

    // Remove the already processed binary file
    delete item.binary['srt_file'];
  }
}
return $input.all();

/**
 * N8N Code Box: SRT Subtitle Parser to JSON
 * Purpose: Parses SRT subtitle content and converts it to structured JSON format
 * Input: Items with 'file_content' property containing SRT formatted text
 * Output: Items with parsed subtitles array and count added to JSON
 */

for (const item of $input.all()) {
  // Get SRT content
  const srtContent = item.json.file_content || '';

  const subtitlesList = [];

  if (srtContent) {
    // Split into blocks by double line break
    const blocks = srtContent.split('\n\n');

    for (const block of blocks) {
      if (!block.trim()) continue;

      // Split into lines and clean
      const lines = block.trim().split('\n').map(line => line.trim()).filter(line => line);

      // Verify we have at least 3 lines
      if (lines.length < 3) continue;

      // Extract index
      const index = parseInt(lines[0]);
      if (isNaN(index)) continue;

      // Extract timestamps
      const timestampLine = lines[1];
      if (!timestampLine.includes('-->')) continue;

      const timeParts = timestampLine.split('-->');
      if (timeParts.length !== 2) continue;

      const startTime = timeParts[0].trim();
      const endTime = timeParts[1].trim();

      // Extract text (can be multiple lines)
      const text = lines.slice(2).join(' ');

      // Create subtitle object
      const subtitle = {
        index: index,
        start: startTime,
        end: endTime,
        text: text
      };

      subtitlesList.push(subtitle);
    }
  }

  // Add to item's JSON
  item.json.subtitles = subtitlesList;
  item.json.subtitles_count = subtitlesList.length;
}
return $input.all();

/**
 * N8N Code Box: Original and Translated Subtitle Merger
 * Purpose: Merges original subtitles with translations based on index, preserving timing data
 * Input: Items with nested data containing 'subtitles' and 'subtitles_translated' arrays
 * Output: Items with merged subtitles using original timing and translated text
 */

const inputData = $input.all();
const results = [];
for (const item of inputData) {
  const data = item.json.data?.[0] || item.json; // Fallback to root if nested structure missing

  // Extract subtitle arrays
  const originalSubtitles = data.subtitles || [];
  const translatedSubtitles = data.subtitles_translated || [];

  // Create translation map by index for fast lookup
  const translationsMap = {};
  translatedSubtitles.forEach(translation => {
    translationsMap[translation.index] = translation.text;
  });

  // Merge subtitles: use original timing + translated text
  const mergedSubtitles = originalSubtitles.map(subtitle => {
    const translatedText = translationsMap[subtitle.index];

    return {
      index: subtitle.index,
      start: subtitle.start,
      end: subtitle.end,
      text: translatedText || subtitle.text // Use translation if exists, otherwise original
    };
  });

  // Create the result
  results.push({
    json: {
      merged_subtitles: mergedSubtitles,
      target_lang: data.target_lang,
      srt_file: data.srt_file,
      total_subtitles: mergedSubtitles.length
    }
  });
}
return results;

/**
 * N8N Code Box: SRT File Generator from Merged Subtitles
 * Purpose: Converts merged subtitle data back to SRT format and creates binary file
 * Input: Items with 'merged_subtitles' array containing subtitle objects
 * Output: Items with SRT content as text and binary file, plus metadata (lines, size)
 */

// Generate SRT file from merged subtitles
for (const item of $input.all()) {
  const mergedSubtitles = item.json.merged_subtitles || [];

  if (mergedSubtitles.length === 0) {
    item.json.srt_content = '';
    item.json.srt_lines = 0;
    continue;
  }

  // Sort by index to ensure correct order
  const sortedSubtitles = mergedSubtitles.sort((a, b) => a.index - b.index);

  // Generate SRT content
  const srtBlocks = [];

  for (const subtitle of sortedSubtitles) {
    // SRT format:
    // Subtitle number
    // start_timestamp --> end_timestamp
    // Subtitle text
    // Empty line

    const srtBlock = `${subtitle.index}
${subtitle.start} --> ${subtitle.end}
${subtitle.text}`;

    srtBlocks.push(srtBlock);
  }

  // Join blocks with double line break
  const srtContent = srtBlocks.join('\n\n');

  // Add SRT content to item
  item.json.srt_content = srtContent;
  item.json.srt_lines = srtBlocks.length;
  item.json.srt_size = srtContent.length;

  // Create binary SRT file
  const originalFileName = item.json.srt_file.file_name || 'subtitles.srt';
  const targetLang = item.json.target_lang || 'translated';

  // Generate new filename: originalname.TARGET_LANG.srt
  const fileNameWithoutExt = originalFileName.replace(/\.srt$/i, '');
  const newFileName = `${fileNameWithoutExt}.${targetLang}.srt`;

  // Convert to Base64 for binary storage
  const buffer = Buffer.from(srtContent, 'utf-8');
  const base64Content = buffer.toString('base64');

  // Add as binary file
  item.binary = item.binary || {};
  item.binary['translated_srt'] = {
    data: base64Content,
    fileName: newFileName,
    mimeType: 'text/plain'
  };
}
return $input.all();

/**
 * N8N Binary Renaming Step
 * 
 * Renames a binary file to 'srt_file'.
 * Uses 'binary_file_name' (if present) or defaults to 'data'.
 */

const items = $input.all();

for (let i = 0; i < items.length; i++) {
  const item = items[i];
  const binaryName = item.json.binary_file_name || 'data';

  if (item.binary && item.binary[binaryName]) {
    item.binary.srt_file = item.binary[binaryName];
    if (binaryName !== 'srt_file') {
      delete item.binary[binaryName];
    }
  }
}

return items;

